function [dataOut, headerOut] = keepColumns(data, columnsToKeep, header)headerOut = 0;numOut = size(columnsToKeep, 2);dataOut = zeros(size(data,1),numOut);if nargin == 3  headerOut = header;  headerOut.NumOfPar = numOut;  headerOut.par = {};endfor i = 1:numOut  dataOut(:, i) = data(:,columnsToKeep(i));  if nargin == 3    headerOut.par(i) = header.par(columnsToKeep(i));  endendend  