function plotFcs(data, header, predict)%%TODO: add Logicle transformation option% display parameter numbersfor i = 1:header.NumOfPar  fprintf("%2i: %s\n", i, {header.par.name}{i});end% get user selectionx = input("Parameter for x axis: ");y = input("Parameter for y axis: ");fprintf("You've selected a plot of %s vs. %s.\n", {header.par.name}{x}, {header.par.name}{y});% make figurefigure; hold on;xlim([1, 1e4]);ylim([1, 1e4]);% if predict supplied, find positivesif nargin == 3  pos = find(predict==1);  neg = find(predict==0);end%%TODO: determine from header whether the parameter is a linear or log-scaled%%      parameter and display it as such.% clamp values on the bottom to 1, for logarithmic scaledata = max(1,data);% if predict supplied, plot both, else plot all dataif nargin == 3    loglog(data(neg,x), data(neg,y), 'bo', 'MarkerSize', 1);  loglog(data(pos,x), data(pos,y), 'ro', 'MarkerSize', 1);else  loglog(data(:,x), data(:,y), 'bo', 'MarkerSize', 1);endhold off;end