function [split1, split2] = splitData(data, ratio)% determine size of data and data1dataSize = size(data,1);split1Size = round(dataSize * ratio);% find a random set of indices of size num1randSet = randperm(dataSize, split1Size);% generate a pick arraypick = zeros(dataSize, 1);pick(randSet, 1) = 1;% separate the rows according to picksplit1 = data(pick==1,:);split2 = data(pick==0,:);end