function lambda = validateLambda(train, validate, lambdaVec)lamdba = -1;F1 = 0;for i = 1:size(lambdaVec,1)  theta = logRegression(train, lambdaVec(i));  [validateX, validateY] = separateInputOutput(validate);  predictions = predict(validateX, theta);  currF1 = Fscore(validateY, predictions);  if F1 < currF1    F1 = currF1;    lambda = lambdaVec(i);  endendend  