function [theta, lambda] = validateLambda(train, validate, lambdaVec)lambda = -1;theta = zeros(size(train,2)-1,1);F1 = -1;for i = 1:size(lambdaVec,2)  fprintf("Validating lambda: %f\n", lambdaVec(i));  thisTheta = logRegression(train, lambdaVec(i));  [validateX, validateY] = separateInputOutput(validate);  predictions = predict(validateX, thisTheta);  currF1 = Fscore(validateY, predictions);  if F1 < currF1    F1 = currF1;    lambda = lambdaVec(i);    theta = thisTheta;  endendfprintf("Best validation F1: %f\n", F1);end  